// src/components/Chat.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { Box, TextField, Button, Typography, Paper } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

const Chat = () => {
  const [message, setMessage] = useState("");
  const [history, setHistory] = useState([]);
  const chatEndRef = useRef(null);
  // Aqui você deve ter um identificador do usuário (por exemplo, extraído do token do Keycloak)
  const user_id = "usuario_exemplo";

  const sendMessage = async () => {
    if (message.trim() === "") return;
    
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_BACKEND_URL}/chat`, 
        { user_id, message }
      );
      setHistory(response.data.history);
      setMessage("");
    } catch (error) {
      console.error("Erro ao enviar mensagem:", error);
    }
  };

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [history]);

  return (
    <Box sx={{ p: 2, display: 'flex', flexDirection: 'column', height: 'calc(100vh - 64px)' }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Chat com GEN AI</Typography>
      
      <Box sx={{ flexGrow: 1, overflowY: 'auto', mb: 2, p: 2, border: '1px solid #ddd', borderRadius: 2, backgroundColor: '#fafafa' }}>
        {history.map((entry, index) => (
          <Paper 
            key={index} 
            elevation={2} 
            sx={{
              p: 1, mb: 1,
              backgroundColor: entry.sender === "user" ? "#1976d2" : "#e0e0e0",
              color: entry.sender === "user" ? "#fff" : "#000",
              alignSelf: entry.sender === "user" ? 'flex-end' : 'flex-start',
              maxWidth: '80%'
            }}
          >
            <Typography variant="body1">{entry.text}</Typography>
          </Paper>
        ))}
        <div ref={chatEndRef} />
      </Box>
      
      <Box sx={{ display: 'flex' }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Digite sua mensagem..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              sendMessage();
              e.preventDefault();
            }
          }}
        />
        <Button variant="contained" color="primary" onClick={sendMessage} sx={{ ml: 1 }}>
          <SendIcon />
        </Button>
      </Box>
    </Box>
  );
};

export default Chat;
