// src/App.js
import React, { useEffect } from 'react';
import { AppBar, Toolbar, Typography, Button, Container, Box } from '@mui/material';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { useKeycloak } from '@react-keycloak/web';
import LoginButton from './components/LoginButton';

function Dashboard() {
  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>
        Bem-vindo ao Dashboard do AdamChat
      </Typography>
      <Typography variant="body1">
        Aqui você pode interagir com o chat e utilizar a API de GEN AI.
      </Typography>
    </Container>
  );
}

function App() {
  const { keycloak, initialized } = useKeycloak();
  const navigate = useNavigate();

  // Use um efeito para redirecionar ao dashboard se o usuário estiver autenticado
  useEffect(() => {
    if (initialized && keycloak.authenticated) {
      navigate('/dashboard');
    }
  }, [initialized, keycloak.authenticated, navigate]);

  const handleLogout = () => {
    keycloak.logout({ redirectUri: 'http://localhost:3002/' });
  };

  if (!initialized) {
    return <div>Carregando...</div>;
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ backgroundColor: "#1976d2" }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            AdamChat
          </Typography>
          {keycloak.authenticated ? (
            <Button color="inherit" onClick={handleLogout}>
              Deslogar
            </Button>
          ) : (
            <LoginButton />
          )}
        </Toolbar>
      </AppBar>
      <Routes>
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="*" element={<Navigate to="/dashboard" />} />
      </Routes>
    </Box>
  );
}

export default App;
