// src/components/Chat.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import SendIcon from '@mui/icons-material/Send';
import {
  Box,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  AppBar,
  Toolbar,
  Button,
  Typography,
  TextField,
  IconButton,
  Paper,
  Divider
} from '@mui/material';
import { styled } from '@mui/system';

const drawerWidth = 240;

// Lista de agentes (exemplo)
const agents = [
  { id: 1, name: 'Agente Alpha' },
  { id: 2, name: 'Agente Beta' },
  { id: 3, name: 'Agente Gamma' },
];

// Estilização para as mensagens do usuário e da AI
const MessageBubble = styled(Paper)(({ theme, sender }) => ({
  padding: theme.spacing(1, 2),
  backgroundColor: sender === 'user' ? theme.palette.primary.main : theme.palette.grey[300],
  color: sender === 'user' ? theme.palette.primary.contrastText : theme.palette.text.primary,
  maxWidth: '80%',
  alignSelf: sender === 'user' ? 'flex-end' : 'flex-start',
  marginBottom: theme.spacing(1),
  borderRadius: theme.spacing(2),
}));

const Chat = () => {
  const [message, setMessage] = useState("");
  const [history, setHistory] = useState([]);
  const [selectedAgent, setSelectedAgent] = useState(agents[0]);  // Adicionado para agente selecionado
  const chatEndRef = useRef(null);
  // Aqui você deve ter um identificador do usuário (por exemplo, extraído do token do Keycloak)
  const user_id = "usuario_exemplo";

  // Função para atualizar o agente selecionado
  const handleAgentSelect = (agent) => {
    setSelectedAgent(agent);
    // Aqui você pode, por exemplo, recarregar o histórico específico do agente, se necessário.
  };

  const sendMessage = async () => {
    if (message.trim() === "") return;
    
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_BACKEND_URL}/chat`, 
        { user_id, message }
      );
      setHistory(response.data.history);
      setMessage("");
    } catch (error) {
      console.error("Erro ao enviar mensagem:", error);
    }
  };

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [history]);

  return (
    <Box sx={{ p: 2, display: 'flex', flexDirection: 'column', height: 'calc(100vh - 64px)' }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Chat com GEN AI</Typography>

      {/* Menu Lateral para Agentes */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box' },
        }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap>
            Agentes
          </Typography>
        </Toolbar>
        <Divider />
        <List>
          {agents.map((agent) => (
            <ListItem key={agent.id} disablePadding>
              <ListItemButton
                selected={selectedAgent.id === agent.id}
                onClick={() => handleAgentSelect(agent)}
              >
                <ListItemText primary={agent.name} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
      
      {/* Área Principal do Chat */}
      <Box sx={{ flexGrow: 1, overflowY: 'auto', mb: 2, p: 2, border: '1px solid #ddd', borderRadius: 2, backgroundColor: '#fafafa' }}>
        {history.map((entry, index) => (
          <MessageBubble key={index} sender={entry.sender}>
            <Typography variant="body1">{entry.text}</Typography>
          </MessageBubble>
        ))}
        <div ref={chatEndRef} />
      </Box>
      
      {/* Campo de Entrada */}
      <Box sx={{ display: 'flex' }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Digite sua mensagem..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              sendMessage();
              e.preventDefault();
            }
          }}
        />
        <Button variant="contained" color="primary" onClick={sendMessage} sx={{ ml: 1 }}>
          <SendIcon />
        </Button>
      </Box>
    </Box>
  );
};

export default Chat;
