// src/components/Chat.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import SendIcon from '@mui/icons-material/Send';
import { 
  Box, TextField, Button, Typography, Paper, Select, MenuItem, FormControl, InputLabel 
} from '@mui/material';
import { styled } from '@mui/system';

const MessageBubble = styled(Paper)(({ theme, sender }) => ({
  padding: theme.spacing(1, 2),
  backgroundColor: sender === 'user' ? theme.palette.primary.main : theme.palette.grey[300],
  color: sender === 'user' ? theme.palette.primary.contrastText : theme.palette.text.primary,
  maxWidth: '80%',
  alignSelf: sender === 'user' ? 'flex-end' : 'flex-start',
  marginBottom: theme.spacing(1),
  borderRadius: theme.spacing(2),
}));

const Chat = () => {
  const [message, setMessage] = useState("");
  const [history, setHistory] = useState([]);
  const [provider, setProvider] = useState("chatgpt");
  const chatEndRef = useRef(null);
  const user_id = "usuario_exemplo";

  const sendMessage = async () => {
    if (message.trim() === "") return;
    
    try {
      const response = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/chat`, {
        user_id,
        message,
        provider,
      });
      // Se o endpoint retornar o histórico completo
      setHistory(response.data.history);
      setMessage("");
    } catch (error) {
      console.error("Erro ao enviar mensagem:", error);
    }
  };

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [history]);

  return (
    <Box sx={{ p: 2, display: 'flex', flexDirection: 'column', height: 'calc(100vh - 64px)' }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Chat com GEN AI</Typography>

      {/* Dropdown para selecionar o provider */}
      <FormControl sx={{ mb: 2, minWidth: 200 }}>
        <InputLabel id="provider-select-label">Provider</InputLabel>
        <Select
          labelId="provider-select-label"
          value={provider}
          label="Provider"
          onChange={(e) => setProvider(e.target.value)}
        >
          <MenuItem value="chatgpt">ChatGPT</MenuItem>
          <MenuItem value="gemini">Gemini</MenuItem>
          <MenuItem value="outra_api">Outra API</MenuItem>
        </Select>
      </FormControl>

      {/* Histórico do Chat */}
      <Box
        sx={{
          flexGrow: 1,
          overflowY: 'auto',
          mb: 2,
          p: 2,
          border: '1px solid #ddd',
          borderRadius: 2,
          backgroundColor: '#fafafa',
          display: 'flex',
          flexDirection: 'column'
        }}
      >
        {history.map((entry, index) => (
          <MessageBubble key={index} sender={entry.sender}>
            <Typography variant="body1">{entry.text}</Typography>
          </MessageBubble>
        ))}
        <div ref={chatEndRef} />
      </Box>
      
      {/* Campo de Entrada */}
      <Box sx={{ display: 'flex' }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Digite sua mensagem..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              sendMessage();
              e.preventDefault();
            }
          }}
        />
        <Button variant="contained" color="primary" onClick={sendMessage} sx={{ ml: 1 }}>
          <SendIcon />
        </Button>
      </Box>
    </Box>
  );
};

export default Chat;
