// src/index.js
import React from 'react';
import { createRoot } from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import { ReactKeycloakProvider } from '@react-keycloak/web';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import keycloak from './services/keycloak';
import App from './App';


const initOptions = {
  onLoad: 'login-required',  // força o login se o usuário não estiver autenticado
  checkLoginIframe: false    // desativa verificação via iframe (evita problemas em alguns ambientes)
};

const theme = createTheme({
  palette: {
    mode: 'light', // ou 'dark', conforme sua preferência
    primary: {
      main: '#1976d2', // cor primária
    },
    background: {
      default: '#f5f5f5', // cor de fundo global
      paper: '#ffffff',   // cor para superfícies elevadas (cards, appbars, etc.)
    },
  },
  typography: {
    fontFamily: 'Roboto, sans-serif', // fonte moderna
  },
  components: {
    MuiAppBar: {
      styleOverrides: {
        root: {
          boxShadow: 'none',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none', // remove caixa alta automática
        },
      },
    },
  },
});

const container = document.getElementById('root');
const root = createRoot(container);
root.render(
  <ReactKeycloakProvider authClient={keycloak}>
    <ThemeProvider theme={theme}>
      <BrowserRouter>
        <App />
      </BrowserRouter>
    </ThemeProvider>
  </ReactKeycloakProvider>
);
